import { useState } from "react";
import axios from "axios";
import { Form, Button, Row, Col, FormGroup, FormLabel } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import Message from "../components/Message";
import Loader from "../components/Loader";
import FormContainer from "../components/FormContainer";

function App() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [text, setText] = useState("");

  const contactUs = useSelector((state) => state.contactUs);
  const { loading, error } = contactUs;

  const redirect = location.search ? location.search.split("=")[1] : "/";

  useEffect(() => {
    if (contactUs) {
      history.push(redirect);
    }
  }, [history, contactUs, redirect]);

  const submitHandler = () => {
    if (name.length === 0) {
      alert("Name has left Blank!");
    } else if (email.length === 0) {
      alert("Email has left Blank!");
    } else if (text.length === 0) {
      alert("Text has left Blank!");
    } else {
      const url = "http://localhost/test/formsubmit.php";
      let fData = new FormData();
      fData.append("name", name);
      fData.append("email", email);
      fData.append("text", text);
      axios
        .post(url, fData)
        .then((response) => alert(response.data))
        .catch((error) => alert(error));
    }
  };

  return (
    <FormContainer>
      <h1>Contact Us</h1>
      {error && <Message variant="danger">{error}</Message>}
      {loading && <Loader />}
      <Form onSubmit={submitHandler}>
        <FormGroup controlId="name">
          <FormLabel>Email Address</FormLabel>
          <Form.Control
            type="name"
            placeholder="Enter Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          ></Form.Control>
        </FormGroup>
        <br></br>
        <FormGroup controlId="email">
          <FormLabel>Email</FormLabel>
          <Form.Control
            type="email"
            placeholder="Enter Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          ></Form.Control>
        </FormGroup>
        <br></br>
        <FormGroup controlId="text">
          <FormLabel>Text</FormLabel>
          <Form.Control
            type="text"
            placeholder="Enter Text"
            value={text}
            onChange={(e) => setText(e.target.value)}
          ></Form.Control>
        </FormGroup>
        <br></br>
        <Button type="submit" variant="primary">
          Submit
        </Button>
      </Form>
      <Row className="py-3"></Row>
    </FormContainer>
  );
}

export default App;
