import React, { useState } from "react";
import { Form, Button, Row, FormGroup, FormLabel } from "react-bootstrap";
import FormContainer from "../components/FormContainer";
import { useHistory } from "react-router-dom"; // Importing useHistory hook

const ContactScreen = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [text, setText] = useState("");
  const [error, setError] = useState(""); 
  const [successMessage, setSuccessMessage] = useState("");
  const history = useHistory(); // To use history for redirection

  const handleSubmit = async (event) => {
    event.preventDefault();

    const contactData = {
      name: name, 
      email: email,
      message: text, 
    };
    const token = localStorage.getItem("token");

    // If no token, show error and don't submit
    if (!token) {
      setError("You must be logged in to submit the form.");
      return; 
    }

    try {
      const response = await fetch('/api/contacts', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`, 
        },
        body: JSON.stringify(contactData),
      });

      if (!response.ok) {
        const errorData = await response.json(); 
        throw new Error(errorData.message || 'Error submitting form'); 
      }

      // Clear form fields after successful submission
      setName('');
      setEmail('');
      setText('');
      setSuccessMessage('Your contact form was submitted successfully!');
      setError(''); 

     
    } catch (error) {
      console.error('Error submitting form:', error.message); 
      setError(error.message); 
      setSuccessMessage(''); 
    }
  };

  // Clear error message when the user starts typing again
  const handleInputChange = (setter) => (e) => {
    setter(e.target.value);
    if (error) {
      setError(""); // Clear error message if user starts typing again
    }
  };

  return (
    <FormContainer>
      <h1>Contact Us</h1>
      {error && <p style={{ color: "red" }}>{error}</p>} 
      {successMessage && <p style={{ color: "green" }}>{successMessage}</p>}
      <Form onSubmit={handleSubmit}>
        <FormGroup controlId="name">
          <FormLabel>Name</FormLabel>
          <Form.Control
            type="text"
            placeholder="Enter Name"
            value={name}
            onChange={handleInputChange(setName)} // Using the helper function
          />
        </FormGroup>
        <br />
        <FormGroup controlId="email">
          <FormLabel>Email</FormLabel>
          <Form.Control
            type="email"
            placeholder="Enter Email"
            value={email}
            onChange={handleInputChange(setEmail)} // Using the helper function
          />
        </FormGroup>
        <br />
        <FormGroup controlId="text">
          <FormLabel>Message</FormLabel>
          <Form.Control
            as="textarea"
            placeholder="Enter Message"
            value={text}
            onChange={handleInputChange(setText)} // Using the helper function
          />
        </FormGroup>
        <br />
        <Button type="submit" variant="primary">
          Submit
        </Button>
      </Form>
      <Row className="py-3"></Row>
    </FormContainer>
  );
};

export default ContactScreen;
